generated.css:
html {
  font-size: 62.5%;
}

body {
  font-family: Helvetica, Arial, sans-serif;
  font-size: 1.5rem;
  background: #889ecf;
}

button {
  cursor: pointer;
}

button:disabled {
  cursor: default;
}

.header {
  background-color: white;
  color: #336699;
  padding: 1.5rem;
  margin-bottom: 4rem;
}

.header__title {
  font-size: 2.5rem;
  margin: 0;
}

.header__subtitle {
  color: #336699;
  font-size: 1.5rem;
  font-weight: 500;
  margin: 0;
}

.container {
  max-width: 60rem;
  margin: 0 auto;
  padding: 0 1.5rem;
}

.big-button {
  background-color: #336699;
  border: none;
  border-bottom: 0.6rem solid #254a63;
  color: white;
  font-weight: bold;
  font-size: 1.5rem;
  width: 100%;
  margin-bottom: 1.5rem;
  padding: 1rem;
}

.big0button:disabled {
  opacity: 0.5;
}

.button {
  background-color: #336699;
  border: none;
  border-bottom: 0.3rem solid #254a63;
  color: white;
  font-weight: 500;
  padding: 1rem;
}

.button--link {
  background-color: transparent;
  border: none;
  color: #efefef;
  padding: 0;
}

@media (min-width: 45rem) {
  .big-button {
    background-color: #336699;
    border: none;
    border-bottom: 0.6rem solid #254a63;
    color: white;
    font-weight: bold;
    font-size: 2.5rem;
    width: 100%;
    margin-bottom: 2.5rem;
    padding: 1.5rem;
  }
}
.widget {
  background-color: #444655;
  margin-bottom: 4rem;
}

.widget_message {
  color: #efefef;
  margin: 0;
  padding: 2.5rem;
  text-align: center;
  border-bottom: 1px solid #5b5d71;
}

.widget-header {
  background-color: #12213a;
  color: #efefef;
  display: flex;
  justify-content: space-between;
  padding: 1.5rem;
}

.widget-header__title {
  margin: 0;
}

.option {
  border-bottom: 1px solid #5b5d71;
  display: flex;
  justify-content: space-between;
  padding: 1.5rem 1.5rem;
}

.option__text {
  color: white;
  font-weight: 500;
  font-size: 2rem;
  margin: 0;
  word-break: break-all;
  margin-right: 4rem;
}

.modal {
  background-color: #889ecf;
  padding: 4rem;
  color: white;
  max-width: 40rem;
  text-align: center;
  vertical-align: top;
  outline: none;
}

.modal__title {
  margin: 0 0 1rem 0;
}

.modal__body {
  vertical-align: top;
  margin: 0 0 1rem 0;
  font-size: 1.5rem;
  font-weight: 400;
  word-break: break-all;
  min-width: 40rem;
}

.ReactModalPortal > div {
  opacity: 0;
}

.ReactModalPortal .ReactModal__Overlay {
  align-items: center;
  display: flex;
  transition: opacity 200ms ease-in-out;
  justify-content: center;
}

.ReactModalPortal .ReactModal__Overlay--after-open {
  transition: opacity 200ms ease-in-out;
  opacity: 1;
}

.ReactModalPortal .ReactModal__Overlay--before-close {
  opacity: 0;
}

.add-option-error {
  color: #efefef;
  font-style: italic;
  padding: 0 1.5rem;
  margin: 1.5rem 0 0 0;
}

.add-option-form {
  padding: 1rem;
  flex-direction: column;
  display: flex;
}

.add-option__input {
  background: #889ecf;
  border: none;
  border-bottom: 0.3rem solid #254a63;
  flex-grow: 1;
  color: #12213a;
  margin: 0 0 1rem 0;
}

@media (min-width: 45rem) {
  .add-option-form {
    flex-direction: row;
  }

  .add-option__input {
    margin: 0 1rem 0 0;
  }
}
